import './App.css';
import Chart from 'chart.js/auto';

function App() {
  document.addEventListener('click', function(){
    const user_input = document.getElementById('user_input');
    let user_input_value = user_input.value;
    if(user_input_value !== '' && user_input_value.include(/^[0-9,.]+$/)) {
      let val = null;
      val = user_input.value.replace(/\s/g, '');
      val = val.split(',');
      drawChart(makeObj(val));
    }
  });

  return (
    <div className="App">
      <h3>Digite numeros separados por , para gerar um grafico</h3>
      <input id="user_input" type="text" />
      <input id="sender" type="submit" />
      <div><canvas id="mychart"></canvas></div>
    </div>
  );
}

function drawChart(obj){
  const ctx = document.getElementById('mychart');

  new Chart(ctx, {
    type: 'line',
    data: {
      datasets: [{
        abel: 'My First Dataset',
        data: [obj],
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }]
    },
    options: {
      scales: {
        y: {
          beginAtZero: true
        }
      }
    }
  });
}

function makeNumber(arr){
  return arr.forEach(element => {
    return parseFloat(element);
  });
}

function makeObj(arr) {


  return {type: "data",
  timestamp: Date.now(),
  values: makeNumber(arr)};
}

export default App;
